// Simple Fresnel
// Enjoy! :)

// layout must be the first variable.

layout = {
	name: "Fresnel",
	outputType: 'node',
	icon: 'angle',
	width: 200,
	elements: [
		{ name: 'Color A', inputType: 'node' },
		{ name: 'Color B', inputType: 'node' },
		{ name: 'Fresnel Factor', inputType: 'node' },
	]
};

// THREE and TSL (Three.js Shading Language) namespaces are available.

const { color, float, dot, vec3, normalView } = TSL;

function main() {

	const colorA = parameters.get( 'Color A' ) || color( 0xff0000 );
	const colorB = parameters.get( 'Color B' ) || float( 0x0000ff );
	const fresnelFactor = parameters.get( 'Fresnel Factor' ) || float( 1.3 );

	const fresnel = dot( normalView, vec3( 0, 0, 1 ) ).oneMinus().pow( fresnelFactor );

	return fresnel.mix( colorA, colorB );

}

